import React, { useState, useEffect } from "react";
import Cell from "./Cell.tsx";

// Define the type for the grid
type Grid = boolean[][];

const numRows = 30;
const numCols = 50;

const generateEmptyGrid = (): Grid => {
  const rows = [];
  for (let i = 0; i < numRows; i++) {
    rows.push(Array.from(Array(numCols), () => false));
  }
  return rows;
};

const GameOfLife: React.FC = () => {
  const [grid, setGrid] = useState<Grid>(() => generateEmptyGrid());
  const [running, setRunning] = useState<boolean>(false);

  const runSimulation = () => {
    if (!running) return;

    setGrid((g) => {
      return g.map((row, i) =>
        row.map((cell, j) => {
          const neighbors = [
            [i - 1, j - 1],
            [i - 1, j],
            [i - 1, j + 1],
            [i, j - 1],
            [i, j + 1],
            [i + 1, j - 1],
            [i + 1, j],
            [i + 1, j + 1],
          ];

          let liveNeighbors = 0;
          neighbors.forEach(([x, y]) => {
            if (x >= 0 && x < numRows && y >= 0 && y < numCols && g[x][y]) {
              liveNeighbors += 1;
            }
          });

          if (cell && (liveNeighbors < 2 || liveNeighbors > 3)) {
            return false;
          }
          if (!cell && liveNeighbors === 3) {
            return true;
          }
          return cell;
        })
      );
    });

    setTimeout(runSimulation, 1000);
  };

  useEffect(() => {
    if (running) {
        runSimulation();
    }
  }, [running])

  const toggleRunning = () => {
    setRunning(!running);
  };

  const toggleCellState = (row: number, col: number) => {
    const newGrid = grid.map((r, i) =>
      r.map((cell, j) => (i === row && j === col ? !cell : cell))
    );
    setGrid(newGrid);
  };

  return (
    <div>
      <button onClick={toggleRunning}>
        {running ? "Stop" : "Start"}
      </button>
      <button
        onClick={() => setGrid(generateEmptyGrid())}
        disabled={running}
      >
        Clear
      </button>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${numCols}, 20px)`,
        }}
      >
        {grid.map((row, i) =>
          row.map((cell, j) => (
            <Cell
              key={`${i}-${j}`}
              isAlive={cell}
              toggleCell={() => toggleCellState(i, j)}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default GameOfLife;
